'   vAssetCode   VARCHAR2 (20);',
'   vDocDate     DATE;',
'   vPurDate     DATE;',
'   vVendor      VARCHAR2 (20);',
'   vBranch      VARCHAR2 (20);',
'   vRefno       VARCHAR2 (20);',
'   vReqNo       VARCHAR2 (20);',
'   vSerialNo    NUMBER := 0;',
'BEGIN',
'   BEGIN',
'      DOCNUMBER_GENERATION (s_compcd   => :COMPCODE,',
'                            s_modlcd   => ''ST'',',
'                            s_doctyp   => ''FA'',',
'                            s_subtyp   => 1,',
