'   EXCEPTION',
'      WHEN OTHERS',
'      THEN',
'         RAISE_APPLICATION_ERROR (-20001, ''Unable to Generate Asset ID. '');',
'   END;',
'',
'',
'   BEGIN',
'      SELECT d001,',
'             d002,',
'             C003,',
'             C004,',
'             C005,',
'             C006',
'        INTO vDocDate,',
'             vPurDate,',
'             vVendor,',
'             vBranch,',
'             vRefno,',
'             vReqNo',
'        FROM APEX_COLLECTIONS',
'       WHERE COLLECTION_NAME = ''AST_ACQUISITION'';',
'   EXCEPTION',
'      WHEN OTHERS',
'      THEN',
'         RAISE_APPLICATION_ERROR (-20002,',
'                                  ''Unable to Collect Data From Collection. '');',
