'',
'   :PURBRN := vBranch;',
'   :ASIDNM := vAssetId;',
'   :DOCDAT := vDocDate;',
'   --ITMCOD:= ;',
'   --MODLDS:= ;',
'   --AMOUNT:= ;',
'   --QNTITY:= ;',
'   :PURDAT := vPurDate;',
'   :REFNUM := vRefno;',
'   :REQNUM := vReqNo;',
'   :VENCOD := vVendor;',
'   --CHALNO:= ;',
'   --WARFLG:= ;',
'   --WAREXP:= ;',
'   :STATUS := ''APR'';',
'   --REASON:= ;',
'   :OPRSTAMP := :APP_USER;',
