,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Distribution Process'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'   vSerialNo   NUMBER := 0;',
'   vProduct    VARCHAR2 (20);',
'   data_item   apex_application_global.vc_arr2;',
'',
'   CURSOR SINGLE_ITEM (',
'      vProduct IN VARCHAR2)',
'   IS',
'      SELECT PURBRN,',
'             ASTCOD,',
'             ASIDNM,',
'             SERNUM,',
'             DOCDAT,',
'             TAGIDN,',
'             AMOUNT,',
'             DEPAMT,',
'             BOKVAL,',
'             ACCAMT,',
'             LOCTID,',
'             DEPTID,',
'             STATUS,',
'             REASON,',
'             NEWDOC,',
'             ITMDES',
'        FROM STFXDDTL',
'       WHERE     PURBRN = :P18_PURBRN',
'             AND ASTCOD = :P18_ASTCOD',
'             AND TAGIDN = vProduct;',
'',
'BEGIN',
'   data_item := APEX_UTIL.string_to_table (:P18_ITMCOD);',
'',
'   FOR j IN 1 .. data_item.COUNT',
'   LOOP',
'      vProduct := data_item (j);',
'',
'      FOR i IN SINGLE_ITEM (vProduct)',
'      LOOP',
'         vSerialNo := vSerialNo + 1;',
'',
'         IF i.ACCAMT = 0',
'         THEN',
'            BEGIN',
'               INSERT INTO STFXDDTL (PURBRN,',
'                                     ASTCOD,',
'                                     ASIDNM,',
'                                     SERNUM,',
'                                     DOCDAT,',
'                                     TAGIDN,',
'                                     AMOUNT,',
'                                     DEPAMT,',
'                                     BOKVAL,',
'                                     ACCAMT,',
'                                     LOCTID,',
'                                     DEPTID,',
'                                     RECVDT,',
'                                     RECVBY,',
'                                     STATUS,',
'                                     REASON,',
'                                     TRNFDT,',
'                                     PRVDOC,',
'                                     PRVSER,',
'                                     OPRSTAMP,',
'                                     TIMSTAMP,',
'                                     ITMDES)',
'                    VALUES (:P18_DISTRIBUTION_BRN,',
'                            i.ASTCOD,',
'                            i.ASIDNM,',
'                            vSerialNo,',
'                            TRUNC (SYSDATE),                       --i.DOCDAT,',
'                            i.TAGIDN,',
'                            i.AMOUNT,',
'                            i.DEPAMT,',
'                            i.BOKVAL,',
'                            i.ACCAMT,',
'                            i.LOCTID,',
'                            i.DEPTID,',
'                            NULL,                                   --SYSDATE,',
'                            NULL,                                 --:APP_USER,',
'                            ''NEW'',',
'                            i.REASON,',
'                            NULL,                                   --SYSDATE,',
'                            i.NEWDOC,',
'                            i.SERNUM,',
'                            :APP_USER,',
'                            SYSDATE,',
'                            i.ITMDES);',
'            EXCEPTION',
'               WHEN OTHERS',
'               THEN',
'                  RAISE_APPLICATION_ERROR (',
'                     -20001,',
'                     ''Unable to Insert Distributon Data.'' || SQLERRM);',
'            END;',
'',
'',
'            BEGIN',
'               UPDATE STFXDDTL',
'                  SET STATUS = ''TRN'', TRNFDT = SYSDATE',
'                WHERE     PURBRN = i.PURBRN',
'                      AND ASTCOD = i.ASTCOD',
'                      AND TAGIDN = i.TAGIDN;',
'            EXCEPTION',
'               WHEN OTHERS',
'               THEN',
'                  RAISE_APPLICATION_ERROR (',
'                     -20002,',
'                     ''Unable to Update Distributed Data. '');',
'            END;',
'',
'',
'            IF SQL%ROWCOUNT <> 1',
'            THEN',
'               RAISE_APPLICATION_ERROR (-20003,',
'                                        ''Try to Update More Than One Row. '');',
'            END IF;',
'         ELSE',
'            RAISE_APPLICATION_ERROR (',
'               -20004,',
'               ''Provision Process is Pending for '' || i.TAGIDN);',
'         END IF;',
'      END LOOP;',
'   END LOOP;',
'END;'))
,p_process_error_message=>'#SQLERRM_TEXT#'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_success_message=>'Product Distribution Successful.'
);
