,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'New Customer Create'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* Formatted on 24/Dec/19 8:03:44 PM (QP5 v5.227.12220.39754) */',
'DECLARE',
'   vcount                    NUMBER := 0;',
'   nxtptycde                 NUMBER (10);',
'   vIDENTIFY                 VARCHAR2 (100);',
'   vCUSTTYPE                 VARCHAR2 (4);',
'   vPRTYNAME                 VARCHAR2 (60);',
'   vCONTPER1                 VARCHAR2 (30);',
'   vPARTYAD1                 VARCHAR2 (500);',
'   vPARTYAD2                 VARCHAR2 (500);',
'   vMOBILENO                 VARCHAR2 (11);',
'   vSECOND_GUARANTOR_MNAME   VARCHAR2 (50);',
'   vMOBILENO2                VARCHAR2 (11);',
'   vPEMAILID                 VARCHAR2 (50);',
'   vDRIVING_LICENCE_NO       VARCHAR2 (100);',
'   vBIRTHDAT                 DATE;',
'   vANNIVERSARY_DATE         DATE;',
'BEGIN',
'   IF :P26_PARTY_TYPE = ''N''',
'   THEN',
'',
'   BEGIN',
'      SELECT c001,',
'             c002,',
'             c003,',
'             c004,',
'             c005,',
'             c006,',
'             c007,',
'             c008,',
'             c009,',
'             c010,',
'             c011,',
'             d001,',
'             d002',
'        INTO vIDENTIFY,',
'             vCUSTTYPE,',
'             vPRTYNAME,',
'             vCONTPER1,',
'             vPARTYAD1,',
'             vPARTYAD2,',
'             vMOBILENO,',
'             vSECOND_GUARANTOR_MNAME,',
'             vMOBILENO2,',
'             vPEMAILID,',
'             vDRIVING_LICENCE_NO,',
'             vBIRTHDAT,',
'             vANNIVERSARY_DATE',
'        FROM APEX_COLLECTIONS',
'       WHERE COLLECTION_NAME = ''CUSTOMER_CREATE'';',
'       EXCEPTION',
'       WHEN OTHERS THEN',
'       RAISE_APPLICATION_ERROR (-20001, ''New Customer Info. No Found. '');',
'   END;',
'',
'   -- Create New Customer',
'',
'      BEGIN',
'         SELECT COUNT (1)',
'           INTO vcount',
'           FROM SYCOMPTY',
'          WHERE     CUSTMRID = :P28_NEW_CUST_ID',
'                AND PARTYCDE = :P28_NEW_CUSTOMER_CODE',
'                AND COMPCODE = :COMPCODE;',
'      EXCEPTION',
'         WHEN OTHERS',
'         THEN',
'            RAISE_APPLICATION_ERROR (-20001, ''Exist Customer Check Error. '');',
'      END;',
'',
'',
'      IF vcount = 0',
'      THEN',
'         BEGIN',
'            SELECT MAX (TO_NUMBER (SUBSTR (PARTYCDE, 3, 6)))',
'              INTO nxtptycde',
'              FROM SYCOMPTY',
'             WHERE     UPPER (SUBSTR (PARTYCDE, 1, 2)) = UPPER (''CU'')',
'                   AND COMPCODE = :COMPCODE;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               RAISE_APPLICATION_ERROR (',
'                  -20002,',
'                  ''New Customer Code Creation Error1. '');',
'         END;',
'',
'',
'         IF nxtptycde IS NULL',
'         THEN',
'            nxtptycde := 0;',
'         END IF;',
'',
'         nxtptycde := nxtptycde + 1;',
'',
'',
'         :P28_NEW_CUSTOMER_CODE :=',
'            UPPER (''CU'') || LPAD (TO_CHAR (nxtptycde), 6, 0);',
'',
'         BEGIN',
'            SELECT    TO_CHAR (SYSDATE, ''rrrrmmdd'')',
'                   || LPAD (SEQ_CUSTMRID.NEXTVAL + 1, 5, 0)',
'                      a',
'              INTO :P28_NEW_CUSTOMER_ID',
'              FROM DUAL;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               RAISE_APPLICATION_ERROR (',
'                  -20003,',
'                  ''New Customer Code Creation Error2. '');',
'         END;',
'      ELSE',
'         RAISE_APPLICATION_ERROR (-20004, ''Customer Exists. '');',
'      END IF;',
'',
'--RAISE_APPLICATION_ERROr(-20001, :P28_NEW_CUST_ID||''  ''||vPRTYNAME);',
'',
'      BEGIN',
'         INSERT INTO SYCOMPTY (COMPCODE,',
'                               PARTYTYP,',
'                               IDENTIFY,',
'                               CUSTTYPE,',
'                               PRTYNAME,',
'                               CONTPER1,',
'                               PARTYAD1,',
'                               PARTYAD2,',
'                               MOBILENO,',
'                               SECOND_GUARANTOR_MNAME,',
'                               MOBILENO2,',
'                               PEMAILID,',
'                               DRIVING_LICENCE_NO,',
'                               EMPLYDOB,',
'                               ANNIVERSARY_DATE,',
'                               PARTYCDE,',
'                               CUSTMRID,',
'                               OPRSTAMP,',
'                               TIMSTAMP)',
'              VALUES (:COMPCODE,',
'                      ''CU'',',
'                      vIDENTIFY,',
'                      vCUSTTYPE,',
'                      vPRTYNAME,',
'                      vCONTPER1,',
'                      vPARTYAD1,',
'                      vPARTYAD2,',
'                      vMOBILENO,',
'                      vSECOND_GUARANTOR_MNAME,',
'                      vMOBILENO2,',
'                      vPEMAILID,',
'                      vDRIVING_LICENCE_NO,',
'                      vBIRTHDAT,',
'                      vANNIVERSARY_DATE,',
'                      :P28_NEW_CUSTOMER_CODE,',
'                      :P28_NEW_CUSTOMER_ID,',
'                      :APP_USER,',
'                      SYSDATE);',
'      EXCEPTION',
'         WHEN OTHERS',
'         THEN',
'            RAISE_APPLICATION_ERROR (',
'               -20001,',
'               ''New Customer Information Insertion Error. ''|| SQLERRM);',
'      END;',
'   END IF;',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(10074308323475960)
);
