'/* Formatted on 07-Jan-20 8:24:34 PM (QP5 v5.227.12220.39754) */',
'DECLARE',
'   vSerialNo        NUMBER := 0;',
'   vProductTagId    VARCHAR2 (40);',
'   vDocNum          VARCHAR2 (50);',
'   vTagExistCount   NUMBER := 0;',
'',
'   CURSOR REQ_PRODUCT_LIST',
'   IS',
'      SELECT C001 TAG_ID, C002 ITMCOD, C003 ITMDES',
'        FROM APEX_COLLECTIONS',
'       WHERE COLLECTION_NAME = ''AST_TRANSFER_PRODUCT2'';',
'',
'   CURSOR PRODUCT_LIST_DETAILS (vProductTagId IN VARCHAR2)',
'   IS',
'      SELECT PURBRN,',
'             ASTCOD,',
'             ASIDNM,',
'             SERNUM,',
'             DOCDAT,',
'             TAGIDN,',
'             AMOUNT,',
'             DEPAMT,',
'             BOKVAL,',
'             ACCAMT,',
'             LOCTID,',
'             DEPTID,',
'             STATUS,',
'             REASON,',
'             NEWDOC,',
'             ITMDES',
'        FROM STFXDDTL',
'       WHERE PURBRN = :P39_BRANCD_SOURCE AND TAGIDN = vProductTagId;',
'',
'BEGIN',
'   BEGIN',
'      DOCNUMBER_GENERATION (s_compcd   => :COMPCODE,',
'                            s_modlcd   => ''ST'',',
'                            s_doctyp   => ''AR'',',
'                            s_subtyp   => 1,',
'                            s_docdat   => TRUNC (SYSDATE),',
'                            s_loccde   => NULL,',
'                            origmodl   => ''ST'',',
'                            newdocno   => vDocNum);',
'   EXCEPTION',
'      WHEN OTHERS',
'      THEN',
'         RAISE_APPLICATION_ERROR (',
'            -20001,',
'            ''Unable to Generate Asset Transfer Request Code. '');',
'   END;',
'',
'',
'   FOR j IN REQ_PRODUCT_LIST',
'   LOOP',
'      vProductTagId := j.TAG_ID;',
'',
'      BEGIN',
'         SELECT COUNT (1)',
'           INTO vTagExistCount',
'           FROM STFXDREQ',
'          WHERE     BRNCDE = :P39_BRANCD',
'                AND REQBRB = :P39_BRANCD_SOURCE',
'                AND TAGIDN = vProductTagId',
'                AND STATUS = ''NEW'';',
'      EXCEPTION',
'         WHEN OTHERS',
'         THEN',
'            RAISE_APPLICATION_ERROR (',
'               -20001,',
'               ''Problem Found in Check Requested Data. '');',
'      END;',
'',
'--raise_application_error(-20001, vTagExistCount);',
'      IF vTagExistCount <> 0',
'      THEN',
'         RAISE_APPLICATION_ERROR (',
'            -20001,',
'               ''Request Already Exist for Tagging ID ''',
'            || j.TAG_ID',
'            || ''. Please Check on Transfer Approval Screen. '');',
'      ELSE',
'         FOR i IN PRODUCT_LIST_DETAILS (vProductTagId)',
'         LOOP',
'            vSerialNo := vSerialNo + 1;',
'',
'            -- RAISE_APPLICATION_ERROR(-20001,vProductTagId ||'' - ''|| :P39_BRANCD_SOURCE || '' - ''||i.ASTCOD);',
'',
'            --IF i.ACCAMT = 0',
'            --THEN',
'            BEGIN',
'               INSERT INTO STFXDREQ (BRNCDE,',
'                                     REQBRB,',
'                                     DOCNUM,',
'                                     DOCTYP,',
'                                     SUBTYP,',
'                                     DOCDAT,',
'                                     ASTCOD,',
'                                     ASIDNM,',
'                                     SERNUM,',
'                                     TAGIDN,',
'                                     ITMCOD,',
'                                     MODLDS,',
'                                     AMOUNT,',
'                                     QNTITY,',
'                                     REFNUM,',
'                                     REQNUM,',
'                                     STATUS,',
'                                     REASON,',
'                                     OPRSTAMP,',
'                                     TIMSTAMP,',
'                                     DIVCOD)',
'                    VALUES (:P39_BRANCD,',
'                            :P39_BRANCD_SOURCE,',
'                            vDocNum,',
'                            ''AR'',',
'                            1,',
'                            TRUNC (SYSDATE),',
'                            i.ASTCOD,',
'                            i.ASIDNM,',
'                            vSerialNo,',
'                            i.TAGIDN,',
'                            j.ITMCOD,',
'                            i.ITMDES,',
'                            i.AMOUNT,',
'                            NULL,',
'                            NULL,',
'                            NULL,',
'                            ''NEW'',',
'                            NULL,',
'                            :APP_USER,',
'                            SYSDATE,',
'                           :COMPCODE);',
'            EXCEPTION',
'               WHEN OTHERS',
'               THEN',
'                  RAISE_APPLICATION_ERROR (',
'                     -20002,',
'                     ''Unable to Insert into Asset Transfer Request Table.''|| SQLERRM);',
'            END;',
'',
'         END LOOP;',
'      END IF;',
'   END LOOP;',
'',
'',
'   APEX_COLLECTION.DELETE_COLLECTION (p_collection_name => ''AST_TRANSFER_REQ'');',
'',
'   APEX_COLLECTION.DELETE_COLLECTION (',
'      p_collection_name => ''AST_TRANSFER_PRODUCT'');',
'',
'   APEX_COLLECTION.DELETE_COLLECTION (',
'      p_collection_name => ''AST_TRANSFER_PRODUCT2'');',
'',
'',
'   :P39_SUCCESS_MSG := ''Transfer Request Successful. Transfer No. '' || vDocNum;',
'END;'))
,p_process_error_message=>'#SQLERRM_TEXT#'
