,p_process_sequence=>10
,p_process_point=>'AFTER_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'User & Approval Level'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* Formatted on 12/Jan/20 7:29:00 PM (QP5 v5.227.12220.39754) */',
'DECLARE',
'   CURSOR ALL_USER_LEVEL',
'   IS',
'      SELECT USERCODE,',
'             APRLEVEL,',
'             LAG (APRLEVEL, 1,0) OVER (ORDER BY APRLEVEL)',
'                PREV_APRLEVEL',
'        FROM NMUSRLEV',
'       WHERE COMPCODE = :COMPCODE AND AUTHTYPE = ''APR'' AND TRDOCTYP = ''AR'';',
'',
'BEGIN',
'   BEGIN',
'      SELECT COUNT (1)',
'        INTO :P40_TOTAL_APRLEVEL',
'        FROM NMUSRLEV',
'       WHERE COMPCODE = :COMPCODE AND AUTHTYPE = ''APR'' AND TRDOCTYP = ''AR'';',
'   EXCEPTION',
'      WHEN OTHERS',
'      THEN',
'         :P40_TOTAL_APRLEVEL := 0;',
'   END;',
'',
'',
'   IF :P40_TOTAL_APRLEVEL > 0',
'   THEN',
'      /*  BEGIN',
'           SELECT USERCODE,',
'                  APRLEVEL,',
'                  LAG (NVL (APRLEVEL, 0), 1) OVER (ORDER BY APRLEVEL)',
'                     PREV_APRLEVEL',
'             --INTO vUserCode, vAprLevel',
'             FROM NMUSRLEV',
'            WHERE COMPCODE = :COMPCODE AND AUTHTYPE = ''APR'' AND TRDOCTYP = ''AR'';',
'        END;',
'',
'        */',
'',
'      BEGIN',
'         SELECT USERCODE, APRLEVEL',
'           INTO :P40_USERCODE, :P40_APRLEVEL',
'           FROM NMUSRLEV',
'          WHERE     COMPCODE = :COMPCODE',
'                AND USERCODE = :APP_USER',
'                AND AUTHTYPE = ''APR''',
'                AND TRDOCTYP = ''AR'';',
'      EXCEPTION',
'         WHEN OTHERS',
'         THEN',
'            NULL;',
'      END;',
'',
'      FOR i IN ALL_USER_LEVEL',
'      LOOP',
'         IF :P40_USERCODE = i.USERCODE',
'         THEN',
'         ',
'            --RAISE_APPLICATION_ERROR(-20003, i.USERCODE||'' - ''||i.PREV_APRLEVEL);',
'            ',
'            :P40_PREVIOUS_APRLEVEL := i.PREV_APRLEVEL;',
'         END IF;',
'      END LOOP;',
'   END IF;',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
);
wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(18892532870957817)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Transfer Request Approval Process'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* Formatted on 12/Jan/20 8:36:00 PM (QP5 v5.227.12220.39754) */',
'DECLARE',
'   vOriBranch         VARCHAR2 (6);',
'   vDocNum            VARCHAR2 (50);',
'   vTagId             VARCHAR2 (50);',
'   vReason            VARCHAR2 (50);',
'   vEntryUser         VARCHAR2 (6);',
'   vAssetCode         VARCHAR2 (9);',
'   vAssetId           VARCHAR2 (20);',
'   vDocDate           DATE;',
'   vProductCode       VARCHAR2 (20);',
'   vProductDes        VARCHAR2 (200);',
'   vAmount            NUMBER;',
'   vAprtotal          NUMBER := 0;',
'   vPrevLevelStatus   VARCHAR2 (3);',
'BEGIN',
'   FOR i IN 1 .. APEX_APPLICATION.G_f08.COUNT',
'   LOOP',
'      --RAISE_APPLICATION_ERROR (-20005, APEX_APPLICATION.G_f11 (i) ||'' - ''||APEX_APPLICATION.G_f09 (i)||'' - ''||APEX_APPLICATION.G_f01 (i));',
'      FOR j IN 1 .. APEX_APPLICATION.G_f11.COUNT',
'      LOOP',
'         IF APEX_APPLICATION.G_f08 (i) = APEX_APPLICATION.G_f11 (j)',
'         THEN',
'            vOriBranch := APEX_APPLICATION.G_f01 (j);',
'            vDocNum := APEX_APPLICATION.G_f02 (j);',
'            vAssetCode := APEX_APPLICATION.G_f05 (j);',
'            vAssetId := APEX_APPLICATION.G_f06 (j);',
'            vTagId := APEX_APPLICATION.G_f07 (j);',
'            vReason := APEX_APPLICATION.G_f09 (j);',
'            --vEntryUser := APEX_APPLICATION.G_f10 (j);',
'            vDocDate := APEX_APPLICATION.G_f13 (j);',
'            vProductCode := APEX_APPLICATION.G_f15 (j);',
'            vProductDes := APEX_APPLICATION.G_f16 (j);',
'            vAmount := APEX_APPLICATION.G_f17 (j);',
'',
'            --RAISE_APPLICATION_ERROR (-20005, APEX_APPLICATION.G_f11 (j) ||'' - ''||vTagId||'' - ''||vEntryUser||'' - ''||vReason||'' - ''||vBranch);',
'',
'            --- Previous User Level Status Check ---',
'            BEGIN',
'               SELECT STATUS',
'                 INTO vPrevLevelStatus',
'                 FROM STFXDAPR',
'                WHERE     BRNCDE = vOriBranch',
'                      AND DOCNUM = vDocNum',
'                      AND DOCTYP = ''AR''',
'                      AND TAGIDN = vTagId',
'                      --AND STATUS = ''NEW''',
'                      AND APRLEVEL = :P40_PREVIOUS_APRLEVEL;',
'            EXCEPTION',
'            WHEN NO_DATA_FOUND THEN',
'            vPrevLevelStatus := ''APR'';',
'               WHEN OTHERS',
'               THEN',
'                  RAISE_APPLICATION_ERROR (',
'                     -20003,',
'                        ''Unable to Get Previous User Level Status for Document No. ''',
'                     || vDocNum || '' & ''|| vTagId);',
'            END;',
'',
'',
'            IF vPrevLevelStatus = ''NEW''',
'            THEN',
'               RAISE_APPLICATION_ERROR (',
'                  -20001,',
'                     ''Previous Level User Approval is Pending for Document No. ''',
'                  || vDocNum|| '' & ''|| vTagId);',
'            ELSIF vPrevLevelStatus = ''APR''',
'            THEN',
'               BEGIN',
'                  UPDATE STFXDAPR',
'                     SET STATUS = ''APR'',',
'                         REASON = vReason,',
'                         MODIFYBY = :APP_USER,',
'                         MODIFYDT = SYSDATE',
'                   WHERE     BRNCDE = vOriBranch',
'                         AND DOCNUM = vDocNum',
'                         AND DOCTYP = ''AR''',
'                         AND TAGIDN = vTagId',
'                         AND STATUS = ''NEW''',
'                         AND APPRUSER = :P40_USERCODE',
'                         AND APRLEVEL = :P40_APRLEVEL;',
'               EXCEPTION',
'                  WHEN OTHERS',
'                  THEN',
'                     RAISE_APPLICATION_ERROR (',
'                        -20003,',
'                           ''Unable to Update Approval Data In Approval Table for Document No. ''',
'                        || vDocNum|| '' & ''|| vTagId);',
'               END;',
'            END IF;',
'',
'            --- Count Total Approved Data ------',
'            BEGIN',
'               SELECT COUNT (1)',
'                 INTO vAprtotal',
'                 FROM STFXDAPR',
'                WHERE     BRNCDE = vOriBranch',
'                      AND DOCNUM = vDocNum',
'                      AND DOCTYP = ''AR''',
'                      AND TAGIDN = vTagId',
'                      AND STATUS = ''APR'';',
'            EXCEPTION',
'               WHEN OTHERS',
'               THEN',
'                  RAISE_APPLICATION_ERROR (',
'                     -20003,',
'                        ''Unable to Get Total Approval In Approval Table for Document No. ''',
'                     || vDocNum|| '' & ''|| vTagId);',
'            END;',
'',
'',
'            IF :P40_TOTAL_APRLEVEL = vAprtotal',
'            THEN',
'               BEGIN',
'                  UPDATE STFXDREQ',
'                     SET STATUS = ''APR'',',
'                         REASON = vReason,',
'                         MODIFYBY = :APP_USER,',
'                         MODIFYDT = SYSDATE',
'                   WHERE     BRNCDE = vOriBranch',
'                         AND DOCNUM = vDocNum',
'                         AND TAGIDN = vTagId',
'                         AND STATUS = ''NEW'';',
'               EXCEPTION',
'                  WHEN OTHERS',
'                  THEN',
'                     RAISE_APPLICATION_ERROR (',
'                        -20003,',
'                           ''Unable to Update Approval Data In Request Table for Document No. ''',
'                        || vDocNum|| '' & ''|| vTagId);',
'               END;',
'            END IF;',
'         END IF;',
'      END LOOP;',
'   END LOOP;',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(18892888421957820)
,p_process_success_message=>'Approval Successful.'
);
end;
