'   vAccumulationAmt   NUMBER := 0;',
'   vAssetCode         VARCHAR2 (9);',
'   vAssetId           VARCHAR2 (20);',
'BEGIN',
' /*  BEGIN',
'      DOCNUMBER_GENERATION (s_compcd   => :COMPCODE,',
'                            s_modlcd   => ''ST'',',
'                            s_doctyp   => ''AI'',',
