 p_id=>wwv_flow_api.id(6957751311301210)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'process calculation'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'sl number:=1;',
'BEGIN',
'    Declare',
'    CURSOR c_cur is',
'        SELECT A.PURBRN , A.ASTCOD ,A.ASIDNM,B.TYPCOD, A.DOCDAT, A.TAGIDN, A.AMOUNT PURCHASE_VALUE, A.DEPAMT, A.BOKVAL, A.ACCAMT,',
'                    CASE',
'                    WHEN NVL(B.DEPMET,''X'') = ''S'' THEN ',
'                     A.AMOUNT',
'                    WHEN NVL(B.DEPMET,''X'') = ''R'' THEN',
'                     A.BOKVAL',
'                    ELSE',
'                      A.AMOUNT',
'                    END DEP_CAL_AMNT,',
'                    NVL(A.LOCTID,A.PURBRN) LOCTID, NVL(A.RECVDT,A.DOCDAT) RECVDT,A.NEWDOC,',
'                    B.DEPMET, B.DEPRAT,act.DEPHED,act.ACCHED  ',
'        FROM STFXDDTL A, STFXDSTP B ,',
'                                                      (SELECT b.asidnm,a.DEPHED,a.ACCHED  ',
'                                                    FROM stfxditm a, stfxdmas b',
'                                                    where a.itmcod =b.itmcod) act',
'        WHERE A.STATUS = ''ACT''',
'         and act.asidnm = a.asidnm',
'        AND A.DOCDAT <= :P10040019_S_TODATE',
'        AND A.DOCDAT NOT BETWEEN :P10040019_S_FROMDATE AND :P10040019_S_TODATE',
'        AND A.ASTCOD = B.ASTCOD',
'        AND A.PURBRN = CASE ',
'                                            WHEN :P10040019_BRANCH = ''ALL'' THEN A.PURBRN',
'                                            WHEN :P10040019_BRANCH <>''ALL'' THEN :P10040019_BRANCH',
'                                        END ',
'   AND A.PURBRN NOT IN (SELECT DISTINCT PURBRN FROM STFXDPRV WHERE MONTH||YEARNO = TO_CHAR(TO_DATE(:P10040019_MONTH||:P10040019_YEAR,''MMYYYY''),''MMYYYY''))',
'        ORDER BY A.PURBRN , A.DOCDAT, A.ASTCOD ,A.ASIDNM,  A.TAGIDN;',
'',
'   ',
'    ',
'    BEGIN',
'',
'   FOR i IN c_cur',
'    LOOP',
'        :SERNUM := sl;',
'        :PURBRN := i.PURBRN;',
'        :S_TYPECD := i.TYPCOD;',
'        :TAGIDN := i.TAGIDN;',
'        :P_AMNT := i.PURCHASE_VALUE;',
'        :D_AMNT := i.DEPAMT;',
'        :B_AMNT := i.BOKVAL;',
'        :A_AMNT := i.ACCAMT;',
'        :S_METHOD := i.DEPMET;',
'        :VALBAL := i.DEP_CAL_AMNT;',
'        :DEPRAT := i.DEPRAT;',
'        :TAGIDN := i.TAGIDN;',
'        :ASIDNM := I.ASIDNM;',
'        :DOCDAT := i.DOCDAT;',
'        :PRVDTY := ''PR'';',
'        :PRVSTY := 1;',
'        :DRCODE := i.DEPHED;',
'        :CRCODE := i.ACCHED;',
'        ',
'            ',
'            IF ROUND((NVL(i.PURCHASE_VALUE,0) - NVL(i.DEPAMT,0) - NVL(i.ACCAMT,0)),2) >= ROUND(((nvl(i.DEP_CAL_AMNT,0)* (nvl(i.DEPRAT,0))/100) /12)*:P10040019_MONTH,2) THEN',
'            :DEPPRV := ROUND(((nvl(i.DEP_CAL_AMNT,0)*(nvl(i.DEPRAT,0))/100) /12) *:P10040019_MONTH,2); ',
'            ELSE',
'            :DEPPRV :=    ROUND((NVL(i.PURCHASE_VALUE,0) - NVL(i.DEPAMT,0) - NVL(i.ACCAMT,0)),2);',
'            END IF;',
'        ',
'            BEGIN',
'                SELECT BRANNM INTO :S_BRNCHNM',
'                FROM STBANINF ',
'                WHERE BRANCD = i.PURBRN;',
'            EXCEPTION',
'                WHEN NO_DATA_FOUND THEN',
'                 :S_BRNCHNM:=NULL; ',
'                WHEN TOO_MANY_ROWS THEN',
'                 :S_BRNCHNM:=NULL; ',
'                WHEN OTHERS THEN',
'                 :S_BRNCHNM:=NULL;',
'            END;',
'        ',
'        sl := sl+1;',
'        ',
'    END LOOP;',
' END;',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_type=>'NEVER'
);
